settings.colorFunction:
var type = dsData[dsIndex]['type'];
if (type == 'colorpin') {
	var temperature = dsData[dsIndex]['temperature'];
	if (typeof temperature !== undefined) {
	    var percent = (temperature + 60)/120 * 100;
	    return tinycolor.mix('blue', 'red', amount = percent).toHexString();
	}
	return 'blue';
}

settings.markerImageFunction:
var type = dsData[dsIndex]['type'];
if (type == 'thermomether') {
	var res = {
	    url: images[0],
	    size: 40
	}
	var temperature = dsData[dsIndex]['temperature'];
	if (typeof temperature !== undefined) {
	    var percent = (temperature + 60)/120;
	    var index = Math.min(3, Math.floor(4 * percent));
	    res.url = images[index];
	}
	return res;
}

settings.tooltipFunction:
var deviceType = dsData[dsIndex]['deviceType'];
if (typeof deviceType !== undefined) {
    if (deviceType == "energy meter") {
        return '<b>${entityName}</b><br/><b>Energy:</b> ${energy:2} kWt<br/>';
    } else if (deviceType == "thermometer") {
        return '<b>${entityName}</b><br/><b>Temperature:</b> ${temperature:2} 째C<br/>';
    }
}

settings.labelFunction:
var deviceType = dsData[dsIndex]['deviceType'];
if (typeof deviceType !== undefined) {
    if (deviceType == "energy meter") {
        return '<span style="color:orange;">${entityName}, ${energy:2} kWt</span>';
    } else if (deviceType == "thermometer") {
        return '<span style="color:blue;">${entityName}, ${temperature:2} 째C</span>';
    }
}

