}

.sensor-info-detail span {
    margin-right: 20px;
}

.sensor-info-detail small {
    font-weight: 400;
}

.wave {
    position: relative;
    width: 100%;
    height: 100%;
    background-color: #4973ff;
    border-radius: 50%;
    box-shadow: inset 0 0 50px rgba(0, 0, 0, .5);
}

.wave h1 {
    position: absolute;
    text-align: center;
    font-size: 20px;
}

.wave::before,
.wave::after {
    content: '';
    position: absolute;
    width: 200%;
    height: 200%;
    top: 0;
    left: 50%;
    transform: translate(-50%, -75%);
    background-color: #000;
}

.wave::before {
    border-radius: 45%;
    background: rgba(255, 255, 255, 1);
    animation: animate 5s linear infinite;
}

.wave::after {
    border-radius: 40%;
    background: rgba(255, 255, 255, 0.5);
    animation: animate 10s linear infinite;
}


@keyframes animate {
    0% {
        transform: translate(-50%, -75%) rotate(0deg);
    }

    100% {
        transform: translate(-50%, -75%) rotate(360deg);
    }
}

